import {Document} from 'substance'

import DatatableConfigurator from './DatatableConfigurator'

/**
 * The data model for a Stencila Datatable
 *
 * @class      Datatable (name)
 */
class Datatable extends Document {

  constructor () {
    // Call super with schema generated by configurator
    super(new DatatableConfigurator().getSchema())
    // Create a root body container node for the document
    this.create({
      type: 'container',
      id: 'content',
      nodes: []
    })
  }

}

export default Datatable